import React from "react";
import user from "../images/user.png";

const ContactCard = (props) => {
  const { id, name, email } = props.contact;
  return (
    <div className="list-group-item" style={{display: "flex", alignItems: "center", backgroundColor:"#87bdd6"}}>
      <img className="rounded-circle" style={{width: "40px", height: "40px", objectFit: "cover", borderRadius:"50%"}} src={user} alt="Avatar" />
      <div className="content" style={{backgroundColor:"red", marginLeft: "10px"}}>
        <div><strong>{name}</strong></div>
        <div>{email}</div>
      </div>
      <i
        className="bi bi-trash" style={{color:"red", marginLeft:"auto"}}
        onClick={() => props.clickHander(id)}
      ></i>
    </div>
  );
};

export default ContactCard;
