import React from "react";
import {NavLink} from "react-router-dom";

class AddContact extends React.Component {
  state = {
    name: "",
    email: "",
    number: "",
  };

  add = (e) => {
    e.preventDefault();
    if (this.state.name === "" || (this.state.number === "" && this.state.email==="")) {
      alert("Name and atleast one of number and email are mandatory!");
      return;
    }
    this.props.addContactHandler(this.state);
    this.setState({ name: "", number: "", email: "" });
  };
  render() {
    return (
      <div className="addcontactbox border border-secondary text-center">
        <h2>Add Contact</h2>
        <form className="form" onSubmit={this.add}>
          <div className="field mx-2">
            <label for="name">Name</label>
            <input
              type="text"
              className="form-control"
              name="name"
              placeholder="Enter Name"
              value={this.state.name}
              onChange={(e) => this.setState({ name: e.target.value })}
            />
          </div>
          <div className="field mx-2">
            <label for="number">Number</label>
            <input
              type="text"
              name="number"
              className="form-control"
              placeholder="Enter Number"
              value={this.state.number}
              onChange={(e) => this.setState({ number: e.target.value })}
            />
          </div>
          <div className="field">
            <label for="email">Email</label>
            <input
              type="text"
              name="email"
              className="form-control"
              placeholder="Enter Email"
              value={this.state.email}
              onChange={(e) => this.setState({ email: e.target.value })}
            />
          </div>
          <div className="py-2 d-flex">
            <button className="btn btn-primary">Add</button>
            <NavLink to="/">
            <button className="btn btn-secondary mx-2">Go Back</button>
            </NavLink>
          </div>
        </form>
      </div>
    );
  }
}

export default AddContact;
