import React,{useEffect} from "react";
import ContactCard from "./ContactCard";
import {NavLink} from "react-router-dom";
import { useContactsCrud } from "../context/ContactsCrudContext";

const ContactList = (props) => {
  console.log(props);
  const {getContacts} = useContactsCrud();
  const deleteContactHandler = (id) => {
    props.getContactId(id);
  };

  useEffect(() => {
    getContacts();
  },[]);

  const renderContactList = props.contacts.map((contact) => {
    return (
      <ContactCard
        contact={contact}
        clickHandler={deleteContactHandler}
        key={contact.id}
      />
    );
  });
  return (
    <div className="main">
      <h2 className="mx-auto">Contact List
        <NavLink to="/add">
          <button className="btn btn-outline-success float-end btn-sm"><strong>Add Contact</strong></button>
        </NavLink>
      </h2>  
      {props.contacts.length>0 ? (<div className="list-group">{renderContactList}</div>):(<p>No Contacts</p>)}
    </div>
  );
};

export default ContactList;
