import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { uuid } from "uuidv4";
import "./App.css";
import api from "../api/contacts"
import Header from "./Header";
import AddContact from "./AddContact";
import ContactList from "./ContactList";
import ContactDetails from "./ContactDetails";
import EditContact from "./EditContact";
import { ContactsCrudContextProvider } from "../context/ContactsCrudContext";

function App() {

  const [contacts,setContacts] = useState([]);

  const addContactHandler = async (contact) => {
    console.log(contact);
    const request = {
      id: uuid(),
      ...contact
    };
    const response = await api.post("/contacts", request);
    setContacts([...contacts, response.data]);
  };

  const editContactHandler = async (contact) => {
    const response = await api.put(`/contacts/${contact.id}`,contact);
    const {id,name,number,email} = response.data;
    setContacts(contacts.map((contact) => {
      return contact.id === id ? {...response.data} : contact;
    })
    );
  };

  const removeContactHandler = async (id) => {
    await api.delete(`/contacts/${id}`);
    const newContactList = contacts.filter((contact) =>  contact.id !== id
    );

    setContacts(newContactList);
  };


  return (
    <div className="container sm">
      <Router>
        <Header />
        <ContactsCrudContextProvider>
          <Routes>
          <Route exact path="/" element={<ContactList contacts={contacts} getContactId={removeContactHandler} />}/>
          <Route path="/add" element={<AddContact addContactHandler={addContactHandler}/>} />
          <Route path="/edit" element={<EditContact editContactHandler={editContactHandler}/>} />
          <Route path="/contact/:id" element={<ContactDetails contacts={contacts}/>}/>
          </Routes>
        </ContactsCrudContextProvider>
      </Router>
    </div>
  );
}

export default App;
