import React from 'react'

class AddContact extends React.Component {
    state = {
        name: "",
        number: "",
    };

    add = (e) =>{
        e.preventDefault();
        if(this.state.name === "" && this.state.number ===""){
            alert("COME on mannn");
            return;
        }
        this.props.addContactHandler(this.state);
        this.setState({name:"", number: ""});
        console.log(this.state);
    };

    render() {
    return(
        <div className='ui main'>
            <h2>Add Contact</h2>
            <form className='ui form' onSubmit={this.add}>
                <div className='field'>
                    <label>Name</label>
                    <input type='text' name='name' value={this.state.name} placeholder='Name' onChange={(e) => this.setState({name: e.target.value})} />
                </div>
                <div className='field'>
                    <label>Number</label>
                    <input type='text' name='number' value={this.state.number} placeholder='Number' onChange={(e) => this.setState({number: e.target.value})} />
                </div>
                <button className='ui green button'>ADD</button>
            </form>
        </div>
    );
}
}
export default AddContact
